  int len_s = strlen(s);
  int len_t = strlen(t);

  if (len_s != len_t) return false;

  int count[26] = {0};

  for (int i = 0; i < len_s; i++) {
    count[s[i] - 'a']++;
    count[t[i] - 'a']--;
  }

  for (int i = 0; i < 26; i++) {
