solution.C:
char*** groupAnagrams(char** strs, int strsSize, int* returnSize, int** returnColumnSizes) {
  // Simple approach using sorting
  char*** result = (char***)malloc(strsSize * sizeof(char**));
  *returnColumnSizes = (int*)malloc(strsSize * sizeof(int));
  *returnSize = 0;

  bool* used = (bool*)calloc(strsSize, sizeof(bool));

  for (int i = 0; i < strsSize; i++) {
    if (used[i]) continue;

    result[*returnSize] = (char**)malloc(strsSize * sizeof(char*));
    int groupSize = 0;

    for (int j = i; j < strsSize; j++) {
      if (used[j]) continue;

      // Check if anagrams by sorting
      char* s1 = strdup(strs[i]);
      char* s2 = strdup(strs[j]);

      // Simple bubble sort
      int len1 = strlen(s1), len2 = strlen(s2);
      if (len1 == len2) {
        for (int x = 0; x < len1-1; x++) {
          for (int y = 0; y < len1-x-1; y++) {
            if (s1[y] > s1[y+1]) {
              char temp = s1[y]; s1[y] = s1[y+1]; s1[y+1] = temp;
            }
            if (s2[y] > s2[y+1]) {
              char temp = s2[y]; s2[y] = s2[y+1]; s2[y+1] = temp;
            }
          }
        }

        if (strcmp(s1, s2) == 0) {
          result[*returnSize][groupSize] = strdup(strs[j]);
          groupSize++;
          used[j] = true;
        }
      }

      free(s1);
      free(s2);
    }

    (*returnColumnSizes)[*returnSize] = groupSize;
    (*returnSize)++;
  }

  free(used);
  return result;
}

