solution.C:
bool isValid(char* s) {
  int len = strlen(s);
  char* stack = (char*)malloc(len + 1);
  int top = -1;

  for (int i = 0; i < len; i++) {
    if (s[i] == '(' || s[i] == '{' || s[i] == '[') {
      stack[++top] = s[i];
    } else {
      if (top == -1) return false;
      if (s[i] == ')' && stack[top] != '(') return false;
      if (s[i] == '}' && stack[top] != '{') return false;
      if (s[i] == ']' && stack[top] != '[') return false;
      top--;
    }
  }

  free(stack);
  return top == -1;
}

