func isValid(s string) bool {
  stack := []rune{}
  mapping := map[rune]rune{
    ')': '(',
    '}': '{',
    ']': '[',
  }

  for _, char := range s {
    if _, found := mapping[char]; found {
      if len(stack) == 0 || stack[len(stack)-1] != mapping[char] {
        return false
      }
      stack = stack[:len(stack)-1]
    } else {
      stack = append(stack, char)
    }
  }

  return len(stack) == 0
}

