bool isValid(string s) {
  stack<char> stack;
  unordered_map<char, char> map = {{')', '('}, {'}', '{'}, {']', '['}};

  for (char c : s) {
    if (map.find(c) != map.end()) {
      if (stack.empty() || stack.top() != map[c]) {
        return false;
      }
      stack.pop();
    } else {
      stack.push(c);
    }
  }

  return stack.empty();
}

