int lengthOfLongestSubstring(char* s) {
  int map[128];
  memset(map, -1, sizeof(map));
  int left = 0, maxLen = 0;

  for (int right = 0; s[right] != '\0'; right++) {
    if (map[s[right]] >= left) {
      left = map[s[right]] + 1;
    }
    map[s[right]] = right;
    int currentLen = right - left + 1;
    if (currentLen > maxLen) {
      maxLen = currentLen;
    }
  }

  return maxLen;
}

