double findMedianSortedArrays(int* nums1, int nums1Size, int* nums2, int nums2Size) {
    // Ensure nums1 is the smaller array
    if (nums1Size > nums2Size) {
        return findMedianSortedArrays(nums2, nums2Size, nums1, nums1Size);
    }

    int x = nums1Size;
    int y = nums2Size;
    int low = 0;
    int high = x;

    while (low <= high) {
        int partitionX = (low + high) / 2;
        int partitionY = (x + y + 1) / 2 - partitionX;

        int maxX = (partitionX == 0) ? -1000000000 : nums1[partitionX - 1];
        int maxY = (partitionY == 0) ? -1000000000 : nums2[partitionY - 1];

        int minX = (partitionX == x) ? 1000000000 : nums1[partitionX];
        int minY = (partitionY == y) ? 1000000000 : nums2[partitionY];

        if (maxX <= minY && maxY <= minX) {
            if ((x + y) % 2 == 0) {
                double max_of_left = maxX > maxY ? maxX : maxY;
                double min_of_right = minX < minY ? minX : minY;
                return (max_of_left + min_of_right) / 2.0;
            } else {
                double max_of_left = maxX > maxY ? maxX : maxY;
                return max_of_left;
            }
        } else if (maxX > minY) {
            high = partitionX - 1;
        } else {
            low = partitionX + 1;
        }
    }

    return 0.0;
}

