func findMedianSortedArrays(nums1 []int, nums2 []int) float64 {
    if len(nums1) > len(nums2) {
        nums1, nums2 = nums2, nums1
    }

    x, y := len(nums1), len(nums2)
    low, high := 0, x

    for low <= high {
        partitionX := (low + high) / 2
        partitionY := (x + y + 1) / 2 - partitionX

        var maxX, maxY int
        if partitionX == 0 {
            maxX = -1000000 // Representing negative infinity
        } else {
            maxX = nums1[partitionX-1]
        }

        if partitionY == 0 {
            maxY = -1000000 // Representing negative infinity
        } else {
            maxY = nums2[partitionY-1]
        }

        var minX, minY int
        if partitionX == x {
            minX = 1000000 // Representing positive infinity
        } else {
            minX = nums1[partitionX]
        }

        if partitionY == y {
            minY = 1000000 // Representing positive infinity
        } else {
            minY = nums2[partitionY]
        }

        if maxX <= minY && maxY <= minX {
            if (x+y)%2 == 0 {
                return float64(max(maxX, maxY) + min(minX, minY)) / 2.0
            } else {
                return float64(max(maxX, maxY))
            }
        } else if maxX > minY {
            high = partitionX - 1
        } else {
            low = partitionX + 1
        }
    }

    return 0.0
}

func max(a, b int) int {
