solution.C:
char*** solveNQueens(int n, int* returnSize, int** returnColumnSizes) {
    *returnSize = 0;
    char*** result = (char***)malloc(1000 * sizeof(char**));
    *returnColumnSizes = (int*)malloc(1000 * sizeof(int));

    // Create board
    char** board = (char**)malloc(n * sizeof(char*));
    for (int i = 0; i < n; i++) {
        board[i] = (char*)malloc((n + 1) * sizeof(char));
        for (int j = 0; j < n; j++) {
            board[i][j] = '.';
        }
        board[i][n] = '\0';
    }

    // Check if position is valid
    bool isValid(char** board, int row, int col) {
        // Check column
        for (int i = 0; i < row; i++) {
            if (board[i][col] == 'Q') return false;
        }

        // Check upper left diagonal
        for (int i = row - 1, j = col - 1; i >= 0 && j >= 0; i--, j--) {
            if (board[i][j] == 'Q') return false;
        }

        // Check upper right diagonal
        for (int i = row - 1, j = col + 1; i >= 0 && j < n; i--, j++) {
            if (board[i][j] == 'Q') return false;
        }

        return true;
    }

    // Add solution to result
    void addSolution(char** board) {
        char** solution = (char**)malloc(n * sizeof(char*));
        for (int i = 0; i < n; i++) {
            solution[i] = (char*)malloc((n + 1) * sizeof(char));
            strcpy(solution[i], board[i]);
        }
        result[*returnSize] = solution;
        (*returnColumnSizes)[*returnSize] = n;
        (*returnSize)++;
    }

    // Backtracking function
    void backtrack(int row) {
        if (row == n) {
            addSolution(board);
            return;
        }

        for (int col = 0; col < n; col++) {
            if (isValid(board, row, col)) {
                board[row][col] = 'Q';
                backtrack(row + 1);
                board[row][col] = '.';
            }
        }
    }

    backtrack(0);

    // Free board
    for (int i = 0; i < n; i++) {
        free(board[i]);
    }
    free(board);

    return result;
}

