        if row == n:
            # Create solution without spaces
            solution = []
            for r in board:
                solution.append(''.join(r))
            result.append(solution)
            return

        for col in range(n):
            if is_valid(row, col):
                board[row][col] = 'Q'
                backtrack(row + 1)
                board[row][col] = '.'

    backtrack(0)
    return result

